function burgerMenu(){const e=document.querySelector(".burger"),c=document.querySelector(".menu"),t=document.querySelector("body");e.addEventListener("click",(()=>{c.classList.contains("active")?(c.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked")):(c.classList.add("active"),e.classList.add("active-burger"),t.classList.add("locked"))}));document.querySelectorAll(".menu__item").forEach((i=>{i.addEventListener("click",(function(){c.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked")}))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(c.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}function accordion(){document.querySelectorAll(".accordion__item-trigger").forEach((e=>{e.addEventListener("click",(()=>{const c=e.parentNode;c.classList.contains("accordion__item-active")?c.classList.remove("accordion__item-active"):(document.querySelectorAll(".accordion__item").forEach((e=>e.classList.remove("accordion__item-active"))),c.classList.add("accordion__item-active"))}))}))}burgerMenu(),window.addEventListener("scroll",fixedNav),accordion();